import { Post as IPost } from "./main";
interface Props {
  post: IPost;
}
export const Post = (props: Props) => {
  const { post } = props;
  return (
    <div
      style={{
        border: "1px solid",
        maxWidth: "800px", // or any size you like
        margin: "0 auto",
      }}
    >
      <div className="title">
        <h1>{post.title}</h1>
      </div>
      <div className="body">
        <p>{post.description}</p>
      </div>
      <div
        className="footer"
        style={{ display: "flex", justifyContent: "center" }}
      >
        <p>@{post.username}</p>
        <button> &#128077; </button>
      </div>
    </div>
  );
};
